INSERT INTO `sergenerico` (`ID`, `nombre`, `rol`, `atributos`, `variantes`) VALUES
(1, 'Ares o Marte', 1, '{\r\n\"0\":\"armadura\",\"1\":\"casco\",\"2\":\"coraza\",\"3\":\"lanza\",\"4\":\"espada\",\"5\":\"buitre\",\"6\":\"caballo\",\"7\":\"cuádriga\",\"8\":\"dragón\",\"9\":\"lobo\",\"10\":\"pájaro carpintero\",\"11\":\"perro\"\r\n}', '{\r\n\"0\":\"Solo, con alguno de sus atributos\",\"1\":\"  En la Gigantomaquia* y la Titanomaquia*\",\"2\":\"En la asamblea de los dioses olímpicos\",\"3\":\"En la vuelta de Hefesto al olimpo\",\"4\":\"Ares se enfrenta con Heracles y Atenea por Cicno\",\"5\":\"En Eleusis\",\"6\":\"A la llegada de Heracles al Olimpo\",\"7\":\"En escenas de la guerra de Troya\",\"8\":\"Amores de Ares y Afrodita con Eros revoloteando\",\"9\":\"Con Afrodita, trapado por Hefesto\",\"10\":\"En el juicio de Ixión\",\"11\":\" En el nacimiento de Atenea\",\"12\":\"Con Rea Silva\",\"13\":\"Con su cortejo: Eris, Fobo y Deimo\",\"14\":\"Alegoría: Atenea le aparta de la Paz y la Abundancia\",\"15\":\"Con Zeus\",\"16\":\"En su carro\"\r\n}'),
(2, 'Ártemis o Diana', 1, '{\r\n\"0\":\"alas\",\"1\":\"antorcha\",\"2\":\"armadura\",\"3\":\"arco\",\"4\":\"flechas\",\"5\":\"carcaj\",\"6\":\"jabalina\",\"7\":\"belleza\",\"8\":\"pelo recogido\",\"9\":\"Ciervo\",\"10\":\"corona\",\"11\":\"coturnos*\",\"12\":\"cuernos\",\"13\":\"hidria\",\"14\":\"jabalí\",\"15\":\"juventud\",\"16\":\"luna\",\"17\":\"media luna\",\"18\":\"oso\",\"19\":\"párdalis\",\"20\":\"pechos múltiples\",\"21\":\"perro\",\"22\":\"quitón\",\"23\":\"red\",\"24\":\"serpiente\"\r\n}', '{\r\n\"0\":\"Naciendo de Leto\",\"1\":\"En la asamblea de los dioses\",\"2\":\"En fiestas\",\"3\":\"En la guerra de Troya\",\"4\":\"Con las ninfas o las Cárites\",\"5\":\"Con Heracles y la cierva de Cerinía\",\"6\":\"En su santuario\",\"7\":\"Matando a los hijos Níobe, a Ticio\",\"8\":\" En la Titanomaquia* y la Gigantomaquia*\",\"9\":\"En la competición de Marsias\",\"10\":\" Con diversos personajes como: Acteón, Calisto, Eneo, Hipólito, u Orión.\",\"11\":\" Triunfo de Ártemis\",\"12\":\"Cazando (Ártemis flechadora)\",\"13\":\"Como señora de los animales (Pótnia therôn)\",\"14\":\"Como Ártemis Efesia\",\"15\":\" Bañándose\",\"16\":\"Con Apolo\",\"17\":\"En un carro, como Selene, la Luna\",\"18\":\"Suplicando a Zeus que no la case\",\"19\":\"Descansando\",\"20\":\" Salvando a Ifligenia\"\r\n}'),
(3, 'Atenea o Minerva', 1, '{\n\"0\":\"yelmo\",\"1\":\"escudo\",\"2\":\"lanza\",\"3\":\"belleza\",\"4\":\"caballo\",\"5\":\"égida*\",\"6\":\"gallo\",\"7\":\"gorgóneion*\",\"8\":\"juventud\",\"9\":\"lechuza\",\"10\":\"mochuelo\",\"11\":\"nike\",\"12\":\"olivo\",\"13\":\"peoplo*\",\"14\":\"quitón\",\"15\":\"serpiente\"\n}', '{\r\n\"0\":\"Nacimiento de Atenea de la cabeza de Zeus\",\"1\":\"Armada (atenea Prómachos)\",\"2\":\"Como Atenea Parthénos\",\"3\":\"Pensativa (Atenea Lemnia)\",\"4\":\" En la Amazonomaquia*, Giantomaquia* y Titanomaquia*\",\"5\":\"En la asamblea de los dioses\",\"6\":\"En fiestas y procesiones\",\"7\":\"En el juicio de Paris\",\"8\":\"En la guerra de Troya\",\"9\":\"Perseguida por Hefesto\",\"10\":\"Recibiendo a Erictonio de Gea, la Tierra\",\"11\":\"En la expedición de los argonautas\",\"12\":\"Ayudando a Aquiles, Belerofontes, Diomedes, Heracles, Odiseo, Peleo, Perseo, Teseo y otros héroes\",\"13\":\"Enfrentándose a Posidón*\",\"14\":\"Como estatua en la violación de Casandra\",\"15\":\"Con Aracne, Cadmo, Marsias, Leto\",\"16\":\"Como patrona de los artesanos\",\"17\":\"En el juicio de Ixión\",\"18\":\"Encarnando la virtud, rechazando al Vicio\",\"19\":\"Atenea aparta a Marte de la Paz y la Abundancia\",\"20\":\" Modelando la estatua de un caballo\",\"21\":\"Con un centauro\",\"22\":\"Con Hera\",\"23\":\"Plantando un olivo\"\r\n}'),
(4, 'Apolo o Febo', 1, '{\n\"0\":\"arco\",\"1\":\"carcaj\",\"2\":\"espada\",\"3\":\"ave fenix\",\"4\":\"belleza ideal\",\"5\":\"carro*\",\"6\":\"cierva\",\"7\":\"cisne\",\"8\":\"corzo\",\"9\":\"cuervo\",\"10\":\"delfín\",\"11\":\"dragón\",\"12\":\"gallo\",\"13\":\"gamo\",\"14\":\"gavilán\",\"15\":\"grifo\",\"16\":\"halcón\",\"17\":\"forminge*\",\"18\":\"lira*\",\"19\":\"cítara*\",\"20\":\"plectro*\",\"21\":\"Jacinto (flor)\",\"22\":\"Juventud\",\"23\":\"Lagarto\",\"24\":\"corona de laurel\",\"25\":\"Lobo\",\"26\":\"Los atributos de Helio cuando es asimilado a este dios\",\"27\":\"Milano\",\"28\":\"Mirto\",\"29\":\"Palmera\",\"30\":\"Phiale*\",\"31\":\"Serpiente\",\"32\":\"Serpiente\",\"33\":\"Trípode*\",\"34\":\"arco*\"\n}', '{\r\n\"0\":\"Cazando o matando una lagartija (Apolo Sauróctono)\",\"1\":\" Naciendo de Leto\",\"2\":\"Matando a Pitón, Ticio, los Nióbidas, etc.\",\"3\":\"En la asamblea de los dioses olímpicos\",\"4\":\"En festividades y procesiones\",\"5\":\"Con Ártemis, Dafne, Deidamía, Eos, Hermes, Jacinto, las Horas, las Ninfas, las Cárites, Olimpo, un centauro, etc.\",\"6\":\"En la guerra de Troya\",\"7\":\"Con las Musas (Apolo Musageta)\",\"8\":\"Con las artes\",\"9\":\"Luchando con Heracles\",\"10\":\"En un carro, como Helio\",\"11\":\"En su santuario\",\"12\":\"En la Centauromaquia*, Gigantomaquia*y Titanomaquía*\",\"13\":\"Compitiendo con Marsias o desollándolo\",\"14\":\"Haciendo una libación\",\"15\":\" Apolo citaredo\",\"16\":\"Triunfo de Apolo\"\r\n}'),
(5, 'Afrodita', 1, '{\r\n\"0\":\"Amapola\",\"1\":\"Balanza\",\"2\":\"Belleza\",\"3\":\"Cabra\",\"4\":\"Cetro \",\"5\":\"Cisne\",\"6\":\"Trompeta\",\"7\":\"Concha\",\"8\":\"Corona\",\"9\":\"Delfín\",\"10\":\"Desnudez\",\"11\":\"Eros\",\"12\":\"Estrellas\",\"13\":\"Gorrión\",\"14\":\"Juventud\",\"15\":\"Macho cabrío\",\"16\":\"Manzana\",\"17\":\"Mirto\",\"18\":\"Oca\",\"19\":\"Paloma\",\"20\":\"Rana\",\"21\":\"Perdiz\",\"22\":\"Rosa\",\"23\":\"Tilo\",\"24\":\"Tortuga\",\"25\":\"Zapatilla\"\r\n}', '{\r\n\"0\":\"Nacimiento en el mar (Venus Anadyoméne)\",\"1\":\"Con Erotes/amorcillos\",\"2\":\"Dormida\",\"3\":\"Atrapada con Ares, mientras Hefesto y otros dioses los observan\",\"4\":\"En el juicio de Paris\",\"5\":\"En la guerra de Troya: salvando a paris de Menelao, luchando con otros dioses etc…\",\"6\":\"Con sus amantes: Ares, Adonis, Anquises\",\"7\":\"Con un macho cabrío o cabra (Venus Epitragidia)\",\"8\":\"Con otros dioses, especialmente: Cárites, Eros, Hermes, Náyades, Pan, Tritones, Zeus, Neptuno, etc…\",\"9\":\"Sola, con alguno de sus atributos\",\"10\":\"Con los atributos de la victoria (Venus Victrix)\",\"11\":\"En el lecho, recostada\",\"12\":\"En escenas de baño: agachada, mirándose al espejo (Venus Púdica, Venus Calipigia)\",\"13\":\"Triunfo de Afrodita\",\"14\":\"Sobre una oca o cisne\"\r\n}'),
(6, 'dioniso o baco', 1, '{\n\"0\":\"Asno \",\"1\":\"Belleza\",\"2\":\"Cabello Rizado\",\"3\":\"Cabra\",\"4\":\"Cerdo\",\"5\":\"Corona De hiedra\",\"6\":\"Vid\",\"7\":\"Cuerno O Copa De Libaciones\",\"8\":\"Delfín\",\"9\":\"Delfín\",\"10\":\"Elefante\",\"11\":\"Higuera\",\"12\":\"Huevo\",\"13\":\"Lira*\",\"14\":\"Kimbala*\",\"15\":\"Tympanon*\",\"16\":\"Kántharos*\",\"17\":\"León\",\"18\":\"León\",\"19\":\"Nebrís*\",\"20\":\"Pantera\",\"21\":\"Párdalis*\",\"22\":\"Pátera*\",\"23\":\"Roble\",\"24\":\"Rosa\",\"25\":\"Serpiente\",\"22\":\"Tigre*\",\"23\":\"Tirso\",\"24\":\"Toro\"\n}', '{\r\n\"0\":\"Nacimiento del muslo de Zeus\",\"1\":\"Criado por diferentes personajes: Hermes, las Ninfas, Sileno, etc.\",\"2\":\"Con Sémele\",\"3\":\"En la Gigantomaquia*\",\"4\":\"montado en un leopardo, guepardo o pantera\",\"5\":\"en la asamblea de los dioses\",\"6\":\"en viajes exóticos, en barco, sobre panteras, elefantes, etc.\",\"7\":\"llevando a Hefesto al Olimpo\",\"8\":\"con las cuatro estaciones\",\"9\":\"con Midas, Penteo, Licurgo,Prosimno, Ámpelo, etc.\",\"10\":\"con Ariadna en diferentes escenas\",\"11\":\"con Afrodita\",\"12\":\"Con demeter en el trono\",\"13\":\"tocando la lira*\",\"14\":\"triunfo de Dioniso, en fiestas, con su tíaso*\"\r\n}'),
(7, 'hades, plutón o aidoneo', 1, '{\r\n\"0\":\"Barba\",\"1\":\"carro de oro tirado por caballos\",\"2\":\"casco\",\"3\":\"cetro \",\"4\":\"ciprés\",\"5\":\"cornucopia*\",\"6\":\"corona\",\"7\":\"gallo\",\"8\":\"Granada\",\"9\":\"llave\",\"10\":\"Narciso\",\"11\":\"rostro severo\",\"12\":\"Trono\"\r\n}', '{\r\n\"0\":\"Solo, con alguna de sus atributos\",\r\n\"1\":\"con otros dioses o personajes mitológicos en el Hades*\",\r\n\"2\":\"raptando a Perséfone\",\r\n\"3\":\"en su trono con Perséfone\",\r\n\"4\":\"como Plutón (“Rico”)\",\r\n\"5\":\"En la Titanomaquia* y la Gigantomaquia*\",\r\n\"6\":\"Con Orco\",\r\n\"7\":\"nacimiento de Hades\",\r\n\"8\":\"con Cerbero\",\r\n\"9\":\"en escenas de la Divina Comedia de Dante\",\r\n\"10\":\"marchando al Hades*\"\r\n}'),
(8, 'hefesto o vulcano', 1, '{\r\n\"0\":\"Barba\",\"1\":\"brazos fuertes\",\"2\":\"cojo\",\"3\":\"deforme\",\"4\":\"exomís*\",\"5\":\"fealdad\",\"6\":\"fuelle\",\"7\":\"hacha\",\"8\":\"martillo\",\"9\":\"pecho descubierto\",\"10\":\"Pilos*\",\"11\":\"tenazas\",\"12\":\"yunque\"\r\n}', '{\r\n\"0\":\"Solo, con sus atributos\",\"1\":\"en su fragua\",\"2\":\"en la asamblea de los dioses\",\"3\":\"en luchas con dioses\",\"4\":\"en el nacimiento de Atenea\",\"5\":\"acosando a Atenea\",\"6\":\"encadenando a Prometeo\",\"7\":\"con Dioniso\",\"8\":\"en el nacimiento de Erictonio\",\"9\":\"sobre un asno, llevado al Olimpo por Dioniso, con Ninfas y Silenos\",\"10\":\"en un carro alado\",\"11\":\"entregando a Tetis las armas de Aquiles, a Afrodita las de Eneas\",\"12\":\"Hefesto sorprende a Afrodita y Ares, o los apresa en una red\",\"13\":\"Hefesto sorprende a Afrodita y Ares, o los apresa en una red\"\r\n}'),
(9, 'deméter o ceres ', 1, '{\r\n\"0\":\"trono\",\"1\":\"Adormidera\",\"2\":\"Antorcha\",\"3\":\"cabeza velada\",\"4\":\"canastillo\",\"5\":\"carro tirado por dragones\",\"6\":\"cetro\",\"7\":\"cornucopia*\",\"8\":\"espigas de la mano o en corona\",\"9\":\"Granada\",\"10\":\"Hoz\",\"11\":\"lechón\",\"12\":\"Narciso (flor)\",\"13\":\"serpiente\"\r\n}\r\n', '{\r\n\"0\":\"sentada en su trono\",\"1\":\"en su carro tirado por serpientes\",\"2\":\"con metanira\",\"3\":\"con diversos dioses: Afrodita, Dioniso, Eros, Hermes, Ninfas, Pan, Pomona, Poseidón, etc.\",\"4\":\"lamentándose\",\"5\":\"buscando o encontrando a Perséfone\",\"6\":\"con Triptólemo\",\"7\":\"en Eleusis, en diversos episodios\",\"8\":\"en apoteosis o triunfo\",\"9\":\"con niños como alegoría de los elementos: aire, agua, tierra, fuego\",\"10\":\"alegorías de la agricultura, del verano, de la tierra\"\r\n}\r\n'),
(10, 'hera o juno', 1, '{\n\"0\":\"velo\",\"1\":\"carro\",\"2\":\"cetro\",\"3\":\"cinturón\",\"4\":\"corona\",\"5\":\"cuclillo\",\"6\":\"Granada\",\"7\":\"helicriso*\",\"8\":\"León\",\"9\":\"lirio\",\"10\":\"pavo real\",\"11\":\"peplo*\",\"12\":\"phiale*\",\"13\":\"polos*\",\"14\":\"ternera\",\"15\":\"túnica larga\",\"16\":\"vaca\",\"16\":\"ternera\"\n}\n', '{\r\n\"0\":\"En la asamblea de los dioses\",\"1\":\"sentada en el trono\",\"2\":\"hierogamia* con Zeus\",\"3\":\"en la Titanomaquia* y en la Gigantomaquia*\",\"4\":\"en el juicio de París\",\"5\":\"asistiendo al castigo de Ixión\",\"6\":\"en segundo plano de algunos episodios amorosos de Zeus\",\"7\":\"en un carro tirado por pavos reales\",\"8\":\"castigada por Zeus colgada de las muñecas\",\"9\":\"con prometeo\",\"10\":\"recibiendo la cabeza de Argos de manos de Hermes\",\"11\":\"adornando la cola del pavo real con los ojos de Argos\",\"12\":\"dando de mamar al pequeño Heracles\",\"13\":\"seduciendo a Zeus\",\"14\":\"el pavo real se queja a era\",\"15\":\"pidiendo Afrodita el ceñidor\",\"16\":\"ofrendas a Hera\",\"17\":\"alegoría del aire, de la prosperidad\"\r\n}\r\n'),
(11, 'hermes o mercurio', 1, '{\r\n\"0\":\"Alas\",\"1\":\"bolsa\",\"2\":\"botas aladas\",\"3\":\"caduceo*\",\"4\":\"cítara\",\"5\":\"clámide*\",\"6\":\"cordero\",\"7\":\"cuenco\",\"8\":\"espada curva\",\"9\":\"lira*\",\"10\":\"niño\",\"11\":\"pétaso*\",\"12\":\"casco con alas\",\"13\":\"Pîlos*  (alado)\",\"14\":\"quitonisco*\",\"15\":\"tortuga\"\r\n}\r\n', '{\r\n\"0\":\"como protector de la elocuencia (Hermes Logios)\",\"1\":\"con Afrodita, enseñando a leer a Eros\",\"2\":\"con Herse y Aglauro\",\"3\":\"con Eneas, Erictonio, Heracles, Perseo, etc.\",\"4\":\"con Zeus y la Virtud\",\"5\":\"cómo Hermes trismégistos*\",\"6\":\"Equelo, Basile y Hermes\",\"7\":\"llevando a psique al cielo\",\"8\":\"personificando la oratoria y el saber, junto a Atenea\",\"9\":\"con un papel secundario en múltiples mitos: Pandora, nacimiento de Atenea etcétera\"\r\n}\r\n'),
(12, 'Posidón, Poseidón o Neptuno', 1, '{\r\n\"0\":\"\",\"1\":\"peces\",\"2\":\"delfines\",\"3\":\"hipocampos\",\"4\":\"caballo\",\"5\":\"cabellera abundante\",\"6\":\"corona\",\"7\":\"Tritones\",\"8\":\"Nereidas\",\"9\":\"Proteo\",\"10\":\"glauco\",\"11\":\"toro\",\"12\":\"tridente\"\r\n}\r\n', '{\r\n\"0\":\"Solo, con alguno de sus atributos\",\"1\":\"en la Gigantomaquia*\",\"2\":\"luchando con Tritón\",\"3\":\"en el mar con diferentes Heroes: Tesep, Odiseo, etc\",\"4\":\"en la asablea de los dioses\",\"5\":\"con diferentes dioses: Afrodita, Anfítrite, Tetis, etc\",\"6\":\"con Amimone\",\"7\":\"En su carro, con su cortejo\",\"8\":\"con el navío de Odiseo\",\"9\":\"enfrentándose a Atenea\",\"10\":\"aplacando la tormenta\",\"11\":\"en fuentes\",\r\n\"12\":\"alegoría del agua\",\"13\":\"triunfo de Neptuno\",\"14\":\"montado sobre un toro\",\"15\":\"en su trono\",\"16\":\"regalando a Venecia los tesoros del mar\"\r\n}\r\n'),
(13, 'ZEUS o JÚPITER', 1, '{\r\n\"0\":\"águila\",\"1\":\"barba\",\"2\":\"cabellera abundante\",\"3\":\"cetro\",\"4\":\"con los atributos de su metamorfosis\",\"5\":\"cuernos\",\"6\":\"égida\",\"7\":\"madurez\",\"8\":\"metamorfosis\",\"9\":\"nike\",\"10\":\"rayo\",\"11\":\"roble\",\"12\":\"serpiente\",\"13\":\"toro\",\"14\":\"trono\"\r\n}\r\n', '{\r\n\"0\":\"amamantado por Amaltea, con los Curetes\",\"1\":\"infancia\",\"2\":\"presidiendo la asamblea de los Dioses\",\"3\":\"con dioses, héroes y mortales\",\"4\":\"con sus amantes, matamorfoseado o no\",\"5\":\"en casi todos los hechos mitológicos importantes\",\"6\":\"en la Gigantomaquia*, Titanomaquia* y Tifonomaquia*\",\"7\":\"coronación de Zeus\",\"8\":\"como Júpiter Doliqueno*\",\"9\":\"como Júpiter Cornígero*\",\"10\":\"sentado en el trono\",\"11\":\"lanzando el rayo\",\"12\":\"Bustos\",\"13\":\"Extendiendo su mano sobre madrid\"\r\n}\r\n'),
(14, 'Ana', 2, '{\r\n\"0\":\"libro\",\"1\":\"lirio\",\"2\":\"puerta\"\r\n}\r\n', '{\r\n\"0\":\"Sola, como una matrona, con una túnica verde y un manto rojo\",\"1\":\"con su hija y su nieto\",\"2\":\"con Joaquín, ante la puerta Dorada de Jerusalén\",\"3\":\"En el nacimiento de la Virgen\",\"4\":\"presentando a María en el templo\",\"5\":\"como intercesora entre el mundo y el cielo\"\r\n}\r\n'),
(15, 'Antonio de Padua', 2, '{\r\n\"0\":\"biblia\",\"1\":\"hábito\",\"2\":\"lirio\",\"3\":\"niño Jesús\"\r\n}\r\n', '{\r\n\"0\":\"solo, con alguno de sus atributos\",\"1\":\"con hábito franciscano, con la rama de lirio y el niño Jesús\",\"2\":\"junto a san Francisco\",\"3\":\"representación de algún milagro suyo (peces, mula, joven...)\"\r\n}\r\n'),
(16, 'Bárbara', 2, '{\r\n\"0\":\"cáliz\",\"1\":\"cañones\",\"2\":\"corona\",\"3\":\"espada\",\"4\":\"palma del martirio*\",\"5\":\"bolas de cañones\",\"6\":\"pluma de pavo real\",\"7\":\"rayo\",\"8\":\"torre con 3 ventanas\",\"9\":\"torre\"\r\n}\r\n', '{\r\n\"0\":\"sola, con alguno de sus atributos (la torre puede formar parte de la corona)\",\"1\":\"historia entera de la santa: encierro, persecución, tortura, curación por Cristo, etc\",\"2\":\"en su martirio\"\r\n}\r\n'),
(17, 'Catalina de Alejandría ', 2, '{\r\n\"0\":\"anillo\",\"1\":\"corona\",\"2\":\"espada\",\"3\":\"libro\",\"4\":\"palma del martirio*\",\"5\":\"rueda rota\"\r\n}\r\n', '{\r\n\"0\":\"sola con alguno de sus atributos\",\"1\":\"leyendo un libro\",\"2\":\"torturada; la rueda presenta diferentes formas, siempre con cuchillas y rompiéndose. puede ser única o doble\",\"3\":\"sobre el emperador Majencio, que aparece en el suelo\",\"4\":\"con el niño Jesús en brazos de su madre, por los desposorios místicos*; cuando Jesús aparece como adulto, se trata de Catalina de Siena\",\"5\":\"transportado su cuerpo por varios ángeles hasta el sinaí\"\r\n}\r\n'),
(18, 'Cecilia', 2, '{\r\n\"0\":\"arpa\",\"1\":\"laúd\",\"2\":\"órgano\",\"3\":\"rosas\",\"4\":\"violín\"\r\n}\r\n', '{\r\n\"0\":\"sola, con alguno de sus atributos\",\"1\":\"ante la aparición del ángel a los esposos\",\"2\":\"un ángel ejecuta la música y ella la escucha\",\"3\":\"muerta, con un tajo en el cuello y una corona de rosas o lirios\"\r\n\r\n}\r\n'),
(19, 'Cristóbal', 2, '{\r\n\"0\":\"báculo\",\"1\":\"vara\",\"2\":\"niño jesús\"\r\n}\r\n', '{\r\n\"0\":\"solo, con alguno de sus atributos\",\"1\":\"aparece como un gigantón llevando al niño Jesús en brazos y atravesando un río\",\"2\":\"a veces se acompaña de un árbol como si se tratase de un bastón\"\r\n}\r\n'),
(20, 'Lorenzo', 2, '{\r\n\"0\":\"bolsa\",\"1\":\"caliz\",\"2\":\"cáliz con de piezas de oro\",\"3\":\"cofre\",\"4\":\"cruz\",\"5\":\"cruz procesional*\",\"6\":\"dalmática diaconal\",\"7\":\"evangelario*\",\"8\":\"manípulo\",\"9\":\"parrilla\"\r\n}\r\n', '{\r\n\"0\":\"solo, con alguno de sus atributos\",\"1\":\"repartiendo limosna a los pobres\",\"2\":\"ante el emperador, con los pobres\",\"3\":\"torturado con látigos y garfios\",\"4\":\"asándole en una parrilla\",\"5\":\"con la cabeza coronada de Valerio a sus pies\",\"6\":\"con otros santos (Esteban y Vicente generalmente)\",\"7\":\"apariciones post mortem a otros santos\",\"8\":\"apoteosis (en la gloria)\"\r\n}\r\n'),
(21, 'lucía de siracusa', 2, '{\n\"0\":\"cirios\",\"1\":\"cirios encendidos\",\"2\":\"corona\",\"3\":\"corona de cirios\",\"4\":\"espada en el cuello\",\"5\":\"puñal en el cuello\",\"6\":\"lámpara\",\"7\":\"libro\",\"8\":\"libro de las sagradas escrituras\",\"9\":\"llamas a sus pies\",\"10\":\"ojos\",\"11\":\"ojos en la bandeja\",\"12\":\"cegada\",\"13\":\"palma\",\"14\":\"palma del martirio\",\"15\":\"ciega\"\n}\n', '{\n\"0\":\"sola, con alguno de sus atributos\",\n\"1\":\"en la tumba de santa Águeda\",\n\"2\":\"en cualquiera de las fases de su tormento, ante Pascasio, tirada con cuerdas o bueyes, degollada, comulgando antes de morir, etc\",\n\"3\":\"con otras santas: Apolonia, Cecilia\",\n\"4\":\"enterramiento de la santa\"\n}\n'),
(22, 'Quintín', 2, '{\r\n\"0\":\"clavos\",\"1\":\"clavos en los hombros\",\"2\":\"clavos en la cabeza\",\"3\":\"clavos en las uñas\"\r\n}\r\n', '{\r\n\"0\":\"solo, con alguno de sus atributos\",\"1\":\"caballero, joven e imberbe\",\"2\":\"en diversas torturas\"\r\n}\r\n'),
(23, 'Sebastián', 2, '{\r\n\"0\":\"árbol\",\"1\":\"arco\",\"2\":\"flechas\"\r\n}\r\n', '{\r\n\"0\":\"solo, con alguno de sus atributos\",\r\n\"1\":\"con clámide*, y sin barba en las representaciones mas antiguas\",\r\n\"2\":\"con armadura y barba en representaciones medievales\",\r\n\"3\":\"desnudo, sin barba, joven y atlético, desde el renacimiento\",\r\n\"4\":\"atado a un árbol, de pie, con la mirada hacia el cielo y atravesado con tres flechas\"\r\n}\r\n');
INSERT INTO `usuario` (`ID`, `nombre`, `correo`, `contraseña`) VALUES
(99, 'lorena', 'lorenadiezsegovia@gmail.com', 'as');
